mapper.select:
select , (select c.`value` from t_code c where c.is_deleted = 0 and c.type = 't_line_type' and c.code = t.line_type limit 1) as lineTypeName, (case when main_line = '0' then '无' else (select l.line_name from t_lineInfo l where l.id = t.main_line limit 1) end) as mainLineName, (select c.`value` from t_code c where c.is_deleted = 0 and c.type = 't_line_status' and c.`code` =t.line_status limit 1) as lineStatusName from t_lineInfo t
and t.line_name like concat('%', #{lineName}, '%')
and t.line_length >= #{lineLength}
and t.radius >= #{radius}
and t.line_type in
#{item}
and t.line_status = #{lineStatus}
and t.unit_name like concat('%', #{unitName}, '%')
and t.is_deleted = 0 and exists (select 1 from t_user u where u.is_deleted = 0 and u.id = t.company)
order by t.create_time desc
